// ============================================
// LATIHAN 1: Hello World MCP - Process Request
// ============================================

// Step 1: Ambil data dari request
const inputData = $input.first().json;

// Step 2: Extract nama dari body
// Request bisa datang sebagai:
// - { body: { name: "Ahmad" } }  ‚Üí dari Postman/curl
// - { name: "Ahmad" }            ‚Üí langsung
const body = inputData.body || inputData;
const name = body.name || "Guest";

// Step 3: Validasi - nama tidak boleh kosong
if (name.trim() === "") {
  // Tetap return response, tapi dengan default
  // Di production, bisa throw error
}

// Step 4: Buat response dalam format MCP standar
const mcpResponse = {
  // Status keseluruhan
  success: true,

  // Data utama
  message: `Hello, ${name}! üëã MCP is working perfectly.`,

  // Metadata (informasi tambahan)
  metadata: {
    tool: "mcp-hello",
    version: "1.0.0",
    processedAt: new Date().toISOString(),
    inputReceived: {
      name: name
    }
  },

  // Tips untuk learning
  tips: [
    "Ini adalah response standar MCP",
    "Field 'success' menandakan apakah request berhasil",
    "Field 'metadata' berisi informasi tentang processing",
    "Selamat! Kamu sudah berhasil membuat MCP endpoint pertama üéâ"
  ]
};

// Step 5: Return response
return [{
  json: mcpResponse
}];
